select o_year, sum(case when nation = 'GERMANY' then volume else 0 end) / sum(volume) as mkt_share from ( select extract(year from o_orderdate) as o_year, l_extendedprice * (1 - l_discount) as volume, n2.n_name as nation from tenant_id.part, tenant_id.supplier, tenant_id.lineitem, tenant_id.orders, tenant_id.customer, tenant_id.nation n1, tenant_id.nation n2, tenant_id.region where p_partkey = l_partkey and s_suppkey = l_suppkey and l_orderkey = o_orderkey and o_custkey = c_custkey and c_nationkey = n1.n_nationkey and n1.n_regionkey = r_regionkey and r_name = 'EUROPE' and s_nationkey = n2.n_nationkey and o_orderdate between date '1995-01-01' and date '1996-12-31' and p_type = 'PROMO PLATED STEEL' ) as all_nations group by o_year order by o_year;
select nation, o_year, sum(amount) as sum_profit from ( select n_name as nation, extract(year from o_orderdate) as o_year, l_extendedprice * (1 - l_discount) - ps_supplycost * l_quantity as amount from tenant_id.part, tenant_id.supplier, tenant_id.lineitem, tenant_id.partsupp, tenant_id.orders, tenant_id.nation where s_suppkey = l_suppkey and ps_suppkey = l_suppkey and ps_partkey = l_partkey and p_partkey = l_partkey and o_orderkey = l_orderkey and s_nationkey = n_nationkey and p_name like '%tan%' ) as profit group by nation, o_year order by nation, o_year desc;
select c_custkey, c_name, sum(l_extendedprice * (1 - l_discount)) as revenue, c_acctbal, n_name, c_address, c_phone, c_comment from tenant_id.customer, tenant_id.orders, tenant_id.lineitem, tenant_id.nation where c_custkey = o_custkey and l_orderkey = o_orderkey and o_orderdate >= date '1994-01-01' and o_orderdate < date '1994-01-01' + interval '3' month and l_returnflag = 'R' and c_nationkey = n_nationkey group by c_custkey, c_name, c_acctbal, c_phone, n_name, c_address, c_comment order by revenue desc limit 20;
select ps_partkey, sum(ps_supplycost * ps_availqty) as value from tenant_id.partsupp, tenant_id.supplier, tenant_id.nation where ps_suppkey = s_suppkey and s_nationkey = n_nationkey and n_name = 'MOZAMBIQUE' group by ps_partkey having sum(ps_supplycost * ps_availqty) > ( select sum(ps_supplycost * ps_availqty) * 0.0000002331 from tenant_id.partsupp, tenant_id.supplier, tenant_id.nation where ps_suppkey = s_suppkey and s_nationkey = n_nationkey and n_name = 'MOZAMBIQUE' ) order by value desc;
select l_shipmode, sum(case when o_orderpriority = '1-URGENT' or o_orderpriority = '2-HIGH' then 1 else 0 end) as high_line_count, sum(case when o_orderpriority <> '1-URGENT' and o_orderpriority <> '2-HIGH' then 1 else 0 end) as low_line_count from tenant_id.orders, tenant_id.lineitem where o_orderkey = l_orderkey and l_shipmode in ('SHIP', 'RAIL') and l_commitdate < l_receiptdate and l_shipdate < l_commitdate and l_receiptdate >= date '1995-01-01' and l_receiptdate < date '1995-01-01' + interval '1' year group by l_shipmode order by l_shipmode;
select c_count, count(*) as custdist from ( select c_custkey, count(o_orderkey) from tenant_id.customer left outer join tenant_id.orders on c_custkey = o_custkey and o_comment not like '%express%requests%' group by c_custkey ) as c_orders (c_custkey, c_count) group by c_count order by custdist desc, c_count desc;
select 100.00 * sum(case when p_type like 'PROMO%' then l_extendedprice * (1 - l_discount) else 0 end) / sum(l_extendedprice * (1 - l_discount)) as promo_revenue from tenant_id.lineitem, tenant_id.part where l_partkey = p_partkey and l_shipdate >= date '1994-12-01' and l_shipdate < date '1994-12-01' + interval '1' month;
create view revenue0 (supplier_no, total_revenue) as select l_suppkey, sum(l_extendedprice * (1 - l_discount)) from tenant_id.lineitem where l_shipdate >= date '1995-09-01' and l_shipdate < date '1995-09-01' + interval '3' month group by l_suppkey;
select s_suppkey, s_name, s_address, s_phone, total_revenue from tenant_id.supplier, revenue0 where s_suppkey = supplier_no and total_revenue = ( select max(total_revenue) from revenue0 ) order by s_suppkey;
drop view revenue0;
select p_brand, p_type, p_size, count(distinct ps_suppkey) as supplier_cnt from tenant_id.partsupp, tenant_id.part where p_partkey = ps_partkey and p_brand <> 'Brand#53' and p_type not like 'MEDIUM BURNISHED%' and p_size in (20, 48, 2, 43, 21, 14, 44, 33) and ps_suppkey not in ( select s_suppkey from tenant_id.supplier where s_comment like '%Customer%Complaints%' ) group by p_brand, p_type, p_size order by supplier_cnt desc, p_brand, p_type, p_size;
select sum(l_extendedprice) / 7.0 as avg_yearly from tenant_id.lineitem, tenant_id.part where p_partkey = l_partkey and p_brand = 'Brand#32' and p_container = 'LG DRUM' and l_quantity < ( select 0.2 * avg(l_quantity) from tenant_id.lineitem where l_partkey = p_partkey);
select c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice, sum(l_quantity) from tenant_id.customer, tenant_id.orders, tenant_id.lineitem where o_orderkey in ( select l_orderkey from tenant_id.lineitem group by l_orderkey having sum(l_quantity) > 313 ) and c_custkey = o_custkey and o_orderkey = l_orderkey group by c_name, c_custkey, o_orderkey, o_orderdate, o_totalprice order by o_totalprice desc, o_orderdate limit 100;
select sum(l_extendedprice* (1 - l_discount)) as revenue from tenant_id.lineitem, tenant_id.part where ((p_partkey = l_partkey and p_brand = 'Brand#25' and p_container in ('SM CasE', 'SM BOX', 'SM PACK', 'SM PKG') and l_quantity >= 9 and l_quantity <= 9 + 10 and p_size between 1 and 5 and l_shipmode in ('AIR', 'AIR REG') and l_shipinstruct = 'DELIVER IN PERSON' ) or ( p_partkey = l_partkey and p_brand = 'Brand#55' and p_container in ('MED BAG', 'MED BOX', 'MED PKG', 'MED PACK') and l_quantity >= 16 and l_quantity <= 16 + 10 and p_size between 1 and 10 and l_shipmode in ('AIR', 'AIR REG') and l_shipinstruct = 'DELIVER IN PERSON' ) or ( p_partkey = l_partkey and p_brand = 'Brand#21' and p_container in ('LG CasE', 'LG BOX', 'LG PACK', 'LG PKG') and l_quantity >= 28 and l_quantity <= 28 + 10 and p_size between 1 and 15 and l_shipmode in ('AIR', 'AIR REG') and l_shipinstruct = 'DELIVER IN PERSON' ));
select s_name, s_address from tenant_id.supplier, tenant_id.nation where s_suppkey in (select ps_suppkey from tenant_id.partsupp where ps_partkey in ( select p_partkey from tenant_id.part where p_name like 'tomato%' ) and ps_availqty > ( select 0.5 * sum(l_quantity) from tenant_id.lineitem where l_partkey = ps_partkey and l_suppkey = ps_suppkey and l_shipdate >= date '1997-01-01' and l_shipdate < date '1997-01-01' + interval '1' year ) ) and s_nationkey = n_nationkey and n_name = 'SAUDI ARABIA' order by s_name;
select s_name, count(*) as numwait from tenant_id.supplier, tenant_id.lineitem l1, tenant_id.orders, tenant_id.nation where s_suppkey = l1.l_suppkey and o_orderkey = l1.l_orderkey and o_orderstatus = 'F' and l1.l_receiptdate > l1.l_commitdate and exists ( select * from tenant_id.lineitem l2 where l2.l_orderkey = l1.l_orderkey and l2.l_suppkey <> l1.l_suppkey ) and not exists ( select * from tenant_id.lineitem l3 where l3.l_orderkey = l1.l_orderkey and l3.l_suppkey <> l1.l_suppkey and l3.l_receiptdate > l3.l_commitdate ) and s_nationkey = n_nationkey and n_name = 'UNITED KINGDOM' group by s_name order by numwait desc, s_name limit 100;
select cntrycode, count(*) as numcust, sum(c_acctbal) as totacctbal from ( select substring(c_phone from 1 for 2) as cntrycode, c_acctbal from tenant_id.customer where substring(c_phone from 1 for 2) in ('30', '19', '16', '31', '26', '13', '12') and c_acctbal > ( select avg(c_acctbal) from tenant_id.customer where c_acctbal > 0.00 and substring(c_phone from 1 for 2) in ('30', '19', '16', '31', '26', '13', '12') ) and not exists ( select * from tenant_id.orders where o_custkey = c_custkey ) ) as custsale group by cntrycode order by cntrycode;
